/**
 * Set the value of a given property on a given object if that value is defined
 * @param {object} obj An object to attempt to set a property of
 * @param {string} prop The name of the property to attempt to set
 * @param {any} value The value to set to the object's property (if it exists)
 * @return {boolean} Whether or not the value was defined
 */
declare function setIfThere(obj: object, prop: string, value: any): boolean;
export default setIfThere;
