import debounce from './debounce';

describe('debounce', () => {
  it('should debounce the given function and return the result', async () => {
    const result = [];
    function fnc(): string {
      result.push('foo');
      return 'bar';
    }
    const debounced = debounce(fnc, 50);
    debounced();
    debounced();
    const bar = await debounced();
    expect(result.length).toEqual(1);
    expect(result[0]).toEqual('foo');
    expect(bar).toEqual('bar');
  });
});
