import mapToProp from './mapByProp';

describe('mapToProp', () => {
  it('should map an array to a keyed object as expected', () => {
    const arr = [
      { id: 1, name: 'apple' },
      { id: 2, name: 'banana' }
    ];
    expect(mapToProp(arr, 'id')).toEqual({
      1: { id: 1, name: 'apple' },
      2: { id: 2, name: 'banana' }
    });
    expect(mapToProp(arr, 'name')).toEqual({
      apple: { id: 1, name: 'apple' },
      banana: { id: 2, name: 'banana' }
    });
  });
});
