/**
 * Map a specified array to an object keyed on a specified property's value for each associated item
 *
 * @example
 * ```
 * const arr = [
 *   { id: 1, name: 'apple' },
 *   { id: 2, name: 'banana' }
 * ];
 * const map = mapToProp(arr, 'id');
 * console.log(map); // --> { 1: { id: 1, name: 'apple' }, 2: { id: 2, name: 'banana' } }
 * ```
 *
 * @param {object[]} arr An array of objects to map
 * @param {string} prop The property name to use when keying values on the resulting object
 * @return {object} An object containing each item in the provided array, keyed on the prop value of the associated item
 */
function mapToProp(arr: object[], prop: string): object {
  return arr.reduce((acc, item) => {
    acc[item[prop]] = item;
    return acc;
  }, {});
}

export default mapToProp;
