import setIfThere from './setIfThere';

describe('setIfThere', () => {
  it('should only set the given value if the value is not undefined', () => {
    const foo = { bar: 'a', baz: 'c' };
    setIfThere(foo, 'bar', 'b');
    expect(foo.bar).toEqual('b');

    setIfThere(foo, 'baz', undefined);
    expect(foo.baz).toEqual('c');
  });
});
